/* eslint-disable @typescript-eslint/no-explicit-any */
declare module '*.scss' {
	const content: Record<string, string>
	export default content
}

declare module '*.css' {
	const content: Record<string, string>
	export default content
}

declare module '*.png' {
	const value: any
	export default value
}

declare module '*.jpg' {
	const value: any
	export default value
}

declare module '*.jpeg' {
	const value: any
	export default value
}

declare module '*.gif' {
	const value: any
	export default value
}

declare module '*.svg' {
	const content: any
	export default content
}
